INSERT INTO `products` ( `category_id`, `category_country`, `p_count`, `p_id`, `p_name`, `p_areas`, `p_varieties`, `p_alcohol`, `p_winning`, `p_products`, `p_winery`, `price`) VALUES
( 2, '德國', 5, 'w2012', 'Schloss Lieser - Riesling Kabinett', 'Germany', '100% Riesling', '13%', NULL, '精釀微甜白酒/展現出麗絲玲的典型特質：成熟酸度架構，活潑、帶有些微氣泡的口感，呈現出純淨的果香，爽口且清淡，但入口不顯得單薄。', NULL, 1280),
( 2, '德國', 5, 'w2013', 'Schmitt Söhne - Blue Riesling Sptlese', 'Germany', '100% Riesling', '12.5 %', NULL, '此酒款選取精粹而飽滿的果實，完美的結合了成熟果實的果酸和果糖，散發出濃郁的水果和礦物質香味。',NULL ,  699),
( 2, '智利', 6, 'w3011', 'Cousino Macul - (Varietal) Chardonnay', 'Chile', '100 % Chardonnay', '13%', 'WA 2011 評分: 85 分', '未過桶的 Chardonnay 展現出清新果香味與良好的自然酸度。酒色淺黃帶有綠色光澤，香氣有青蘋果、白桃，還帶點釋迦的香氣，酸度明亮，果香味伴隨著尾韻走，相當持久。', NULL, 599),
( 2, '智利', 6, 'w3011', 'PACO & LOLA 2012', 'Rias Baixas (D.O.)', '100% Albarino ', '12.5% ', 'International Wine & Spirit Awards 2013 Gold medal', '晶瑩的金黃色，邊緣鑲著透亮的綠光，清脆和諧的果香在空間裡遊蕩，青蘋果、梨子、柑橘、桃子以及香草和細緻花香，可愛極了。新鮮飽滿，滑潤的脂質口感如絲絹，熱帶水果和礦物風味彼此和諧。', NULL, 900),
( 3, '法國', 7, 'c1011', 'Joseph Perrier - Cuvée Royale Demi-Sec ', 'Champagne', ' 35% Chardonnay、35% Pinot Noir、30% Pinot Meunier', '14%', NULL, '以微香甜的方式做表現，其中添約 20% 的儲酒是來自我們擁有的 20 個不同村莊，包涵Cumières, Damery, Hautvillers 與 Verneuil，造就了口感的豐富與層次。豐厚的口感在這支香檳中會有甜美的黃桃香氣呈現，微甜的口感非常適合台灣的飲用風情。', NULL, 1990),
( 3, '法國', 7, 'c1012', 'Joseph Perrier - Cuvee Josephine', 'Champagne', NULL, '13%', 'Wine Spectator 評分:92 分', 'Joseph Perrier的鎮莊之寶，葡萄比率從未公開，瓶身為藝術名家設計，高貴精典！帶有通透誘人、華麗馥郁的花朵香氣，令人為之神魂顛倒，另外也散發出成熟的鳳梨、甜桃、杏桃，以及些微的奶油、烤麵包等香氣，尾韻的甘味長且強勁，是款精彩卓越的作品。此酒款曾與Krug、Bollinger R.D.、Louis Roederer Cristal、Dom Perignon同時被Robert Parker評選最佳經典的高檔香檳！', NULL, 1699),
( 3, '法國', 7, 'c1013', 'LANSON Champagne Black Label', 'Champagne', '35% Chardonnay、50% Pinot Noir、15% Pinot Meunier', '12%', '榮獲2015IWSC大賽特優銀牌', '明亮清澈的酒體，爽脆、新鮮帶有白色花朵的香氣，它給以充滿活力與春天氣息的香味，點綴以多種的花蜜芬芳。口感上帶有成熟水果和柑橘的香氣，酒體均衡，順口且令人愉悅。 ', NULL, 2380),
( 3, '義大利', 8, 'c2011', ' Bottega - Petalo Vino Dell’Amore Moscato', 'Italy', '100% Moscato', '12.5%', NULL, '這款迷人的微甜氣泡酒使用很成熟的蜜思嘉白葡萄釀造，葡萄種植在義大利威內多中部的尤佳寧山區 (Euganean Hills) ，在這個地方以種植玫瑰和蘋果聞名。口感香甜而帶些許微微果酸，很適合冰鎮後在炎炎夏日飲用。', NULL, 1388),
( 3, '義大利', 8, 'c2012', 'Castello Banfi - Banfi Brut', 'Italy', ' 50% Pinot Noir、40% Chardonnay、10% Pinot Blanc', '11%', NULL, '完全按照香檳的釀造方法生產，至少24個月的發酵期。由50%黑皮諾、40%的夏多內、以及10%白皮諾混調而成的氣泡酒；淺稻草色中帶點錦緻的珍珠白色，濃郁的芬芳香氣，清爽中帶著優雅的果酸,和諧極緻的平衡感，充滿整個口中，呈現出完美的餘韻。', NULL, 780),
( 3, '義大利', 8, 'c2013', 'ACCADEMIA PROSECCO SPUMANTE DOC', 'Italy', 'PROSECCO', '12.5 %', NULL, '酒色呈麥桿金黃色澤，澄淨清澈；倒入杯中，金黃蘋果、西洋梨及熱帶水果香氣四溢，清爽芳香的均衡口感，令人回味無窮。 ', NULL, 699),
( 3, '西班牙', 9, 'c3011', 'CAVA de VILARNAU Demi Sec', '西班牙', NULL, '11%', NULL, '閃著金色的稻草色澤，如蘋果般甜美爽口的滋味，清爽的柑橘類點綴其中，完美平衡了口感甜度與酸味的表現，無論搭餐或單飲都是不錯的選擇。', NULL, 1280),
( 3, '西班牙', 9, 'c3012', 'CAVA de VILARNAU BRUT RESERVA ROSE', '西班牙', 'Trepat、Pinot Noir', '11%', NULL, '作品加入Pinot Noir葡萄釀製，賦與酒液唯美浪漫的粉紅色澤；氣泡細緻繽紛、酸度完美、散發濃郁的成熟草莓與覆盆子香氣，為餐前開胃酒的最佳選擇。', NULL, 699),
( 3, '西班牙', 9, 'c3013', 'FREIXENET CORDON ROSADO', 'CATALONIA PENEDÈS', 'TREPAT', '11%', NULL, '可以感受到草莓與華麗深厚風味的超人氣甜味玫瑰氣泡酒。 顏色飽滿美麗的玫瑰色。黑葡萄帶出的沉穩單寧味與成熟草莓帶有的自然甜味形成完美的搭配。使用來自地中海沿岸塔拉戈納省的康卡‧巴貝拉地區，擁有濃厚乾無花果和石榴糖漿特殊味道的葡萄品種—Trepat。', NULL, 1290),
( 3, '西班牙', 9, 'c3014', 'Cava Brut Reserva de la Musica', 'Cataluna', '不甜氣泡酒', '11.5%', NULL, '傳統風格的不甜卡瓦酒、洋溢細緻持久的氣泡', NULL, 825),
( 3, '西班牙', 9, 'c3015', 'RONDEL BLUE BRUT', 'CAVA', '不甜氣泡酒', '11.5%', NULL, NULL,'明亮的鮮黃色澤，並擁有細膩綿密的氣泡。充滿成熟水果(蘋果)的香氣，再帶出淡淡的烤吐司和核果香氣，呈現在酒窖熟成的特徵。 美食搭配：適合搭配開胃小點。' , 1890),
( 3, '西班牙', 9, 'c3016', 'Spain Dream Line Premium', 'Cataluna', '不甜氣泡酒', '8.5%', NULL, '特別選用麝香葡萄莫斯卡托，清澈透亮，吸引著我們的是她那耀眼的紅銅色澤。均勻的分子小氣泡，在紅銅色澤中緩慢流動。利用自然發酵和低溫控制保持氣泡酒的品質。鼻子感受到的是清爽、乾淨的氣味；嘴裡品嚐到的是莫斯卡托的風味，淡淡柑橘果香、蜂蜜甜味和微微的酸，視覺、嗅覺、味覺一次滿足。', NULL, 1450);

